        this.defaultHistoricalData = this.defaultData.map(item => ({
          month: item.month,
          year: item.year - 1,
          revenue: Math.floor(item.revenue * (0.7 + Math.random() * 0.4)) // 70-110% del valor actual
        }));
      }
    },
    /**
     * Genera datos históricos basados en los datos actuales
     */
    generateHistoricalData() {
      return this.filteredChartData.map(item => ({
        month: item.month,
        year: item.year - 1,
        // Año anterior
        revenue: Math.floor(item.revenue * (0.7 + Math.random() * 0.4)) // 70-110% del valor actual
      }));
    },
    /**
     * Asegura que todos los elementos de datos tengan un año
     */
    ensureYearInData(data) {
      return data.map(item => {
        if (!item.year) {
          return {
            ...item,
            year: new Date().getFullYear() // Añadir el año actual si no existe
          };
        }
        return item;
      });
    },
    /**
     * Calcula el rango de valores para los dos conjuntos de datos
     */
    calculateDataRange() {
      const allData = [...this.filteredChartData, ...this.filteredHistoricalChartData];
      const maxValue = Math.max(...allData.map(d => d.revenue || 0));
      console.log('Rango de datos:', {
        datosActuales: this.filteredChartData.map(d => d.revenue),
        datosHistóricos: this.filteredHistoricalChartData.map(d => d.revenue),
        maxValue: maxValue
      });
      return {
        maxValue: maxValue || 1
      };
    },
    /**
     * Actualiza las dimensiones del gráfico en función del contenedor
     */
    updateChartDimensions() {
      if (this.$refs.chartContainer) {
        this.chartWidth = this.$refs.chartContainer.clientWidth;
        this.chartHeight = this.$refs.chartContainer.clientHeight;
      }
    },
    /**
     * Gestiona el movimiento del mouse sobre el gráfico
     * Usa un algoritmo de "snap to nearest point" mejorado para evitar cambios erráticos
     */
    handleMouseMove(event) {
      if (!this.filteredChartData.length || !this.dataPoints.length) return;
      const chartRect = this.$refs.chartContainer.getBoundingClientRect();
      const mouseX = event.clientX - chartRect.left;
      const mouseY = event.clientY - chartRect.top;

      // Solo actualizar mouseY, mouseX se actualizará en updateActivePointFromPosition
      this.mouseY = mouseY;

      // Encuentra el punto más cercano al mouse
      this.updateActivePointFromPosition(mouseX);
      this.mouseOver = true;
    },
    /**
     * Gestiona el movimiento táctil sobre el gráfico (para dispositivos móviles)
     */
    handleTouchMove(event) {
      if (!this.filteredChartData.length || !this.dataPoints.length || !event.touches[0]) return;
      const chartRect = this.$refs.chartContainer.getBoundingClientRect();
      const touchX = event.touches[0].clientX - chartRect.left;
      const touchY = event.touches[0].clientY - chartRect.top;

      // Solo actualizar mouseY, mouseX se actualizará en updateActivePointFromPosition
      this.mouseY = touchY;
      this.updateActivePointFromPosition(touchX);
      this.mouseOver = true;

      // Prevenir el desplazamiento de la página durante el uso del gráfico
      event.preventDefault();
    },
    /**
     * Actualiza el punto activo basado en la posición X del mouse/touch
     */
    updateActivePointFromPosition(posX) {
      if (this.dataPoints.length === 0) return;

      // Encontrar el punto más cercano
      let nearestPoint = this.dataPoints[0];
      let minDistance = Math.abs(nearestPoint.x - posX);
      for (const point of this.dataPoints) {
        const distance = Math.abs(point.x - posX);
        if (distance < minDistance) {
          minDistance = distance;
          nearestPoint = point;
        }
      }

      // Actualizar el índice y la posición X
      this.currentMonthIndex = nearestPoint.index;
      this.mouseX = nearestPoint.x;
      this.mouseOver = true;
    },
    /**
     * Restablece la visualización cuando el mouse deja el gráfico
     */
    resetHighlight() {
      this.mouseOver = false;
    },
    /**
     * Formatea un número grande para mostrar (1M, 1B, etc.)
     */
    formatLargeNumber(value) {
      if (value >= 1000000000) {
        return `${(value / 1000000000).toFixed(3).replace(/\.?0+$/, '')}B`;
      } else if (value >= 1000000) {
        return `${(value / 1000000).toFixed(3).replace(/\.?0+$/, '')}M`;
      } else if (value >= 1000) {
        return `${(value / 1000).toFixed(1).replace(/\.0$/, '')}K`;
      }
      return value.toString();
    },
    /**
     * Formatea un valor monetario con separadores de miles
     */
    formatCurrency(value) {
      if (value === undefined || value === null) return '0';
      return new Intl.NumberFormat('es-ES', {
        maximumFractionDigits: 0
      }).format(value);
    },
    /**
     * Formatea un valor de porcentaje
     */
    formatPercent(value) {
      if (value === undefined || value === null) return '0.0%';
      return `${value > 0 ? '+' : ''}${value.toFixed(1)}%`;
    },
    /**
     * Obtiene el valor del punto histórico en la posición actual
     */
    getHistoricalValueAtCurrentIndex() {
      if (!this.showComparison || this.currentMonthIndex < 0 || !this.filteredHistoricalChartData[this.currentMonthIndex]) return 0;
      const historicalValue = this.filteredHistoricalChartData[this.currentMonthIndex].revenue;
      console.log('Valor histórico actual:', {
        índice: this.currentMonthIndex,
        valor: historicalValue,
        datoCompleto: this.filteredHistoricalChartData[this.currentMonthIndex]
      });
      return historicalValue || 0;
    },
    /**
     * Calcula un valor máximo redondeado apropiado para las etiquetas del eje Y
     */
    getRoundedMax(maxValue) {
      // Determinar la magnitud del valor
      const magnitude = Math.pow(10, Math.floor(Math.log10(maxValue)));

      // Redondear hacia arriba para obtener un valor "limpio"
      let roundedMax = Math.ceil(maxValue / magnitude) * magnitude;

      // Si el valor redondeado es mucho mayor que el máximo real, ajustar
      if (roundedMax > maxValue * 1.5) {
        roundedMax = Math.ceil(maxValue / (magnitude / 2)) * (magnitude / 2);
      }
      return roundedMax;
    },
    /**
     * Formatea las etiquetas del eje Y para que sean legibles
     */
    formatAxisLabel(value) {
      return this.formatLargeNumber(value);
    },
    /**
     * Abre/cierra el selector de períodos
     */
    togglePeriodSelector() {
      this.showPeriodSelector = !this.showPeriodSelector;
    },
    /**
     * Cierra el selector de períodos
     */
    closePeriodSelector() {
      this.showPeriodSelector = false;
    },
    /**
     * Selecciona un nuevo período
     */
    selectPeriod(period) {
      this.selectedPeriod = period;
      this.showPeriodSelector = false;
      this.$emit('period-changed', period);
    },
    /**
     * Filtra los datos para mostrar los últimos X días
     */
    filterLastDays(data, days) {
      const now = new Date();
      now.setHours(23, 59, 59, 999); // Final del día actual
      const startDate = new Date(now);
      startDate.setDate(startDate.getDate() - days);
      startDate.setHours(0, 0, 0, 0); // Inicio del día

      return data.filter(item => {
        const itemDate = new Date(item.year, this.monthLabels.indexOf(item.month), item.day || 1);
        return itemDate >= startDate && itemDate <= now;
      }).sort((a, b) => {
        const dateA = new Date(a.year, this.monthLabels.indexOf(a.month), a.day || 1);
        const dateB = new Date(b.year, this.monthLabels.indexOf(b.month), b.day || 1);
        return dateA - dateB;
      });
    },
    /**
     * Filtra los datos para mostrar los últimos X meses
     */
    filterLastMonths(data, months) {
      const now = new Date();
      const startDate = new Date(now);
      startDate.setMonth(now.getMonth() - months + 1); // Ajustamos para incluir el mes actual
      startDate.setDate(1);
      startDate.setHours(0, 0, 0, 0);

      // Filtrar y ordenar los datos
      const filteredData = data.filter(item => {
        const itemDate = new Date(item.year, this.monthLabels.indexOf(item.month), item.day || 1);
        return itemDate >= startDate && itemDate <= now;
      });

      // Ordenar por fecha
      return filteredData.sort((a, b) => {
        const dateA = new Date(a.year, this.monthLabels.indexOf(a.month), a.day || 1);
        const dateB = new Date(b.year, this.monthLabels.indexOf(b.month), b.day || 1);
        return dateA - dateB;
      });
    },
    /**
     * Filtra los datos para mostrar solo el día de hoy
     */
    filterToday(data) {
      const now = new Date();
      const today = new Date(now.getFullYear(), now.getMonth(), now.getDate());
      return data.filter(item => {
        const itemDate = new Date(item.year, this.monthLabels.indexOf(item.month), item.day || 1);
        return itemDate.getFullYear() === today.getFullYear() && itemDate.getMonth() === today.getMonth() && itemDate.getDate() === today.getDate();
      }).sort((a, b) => {
        return (a.hour || 0) - (b.hour || 0);
      });
    },
    /**
     * Filtra los datos para mostrar solo el día de ayer
     */
    filterYesterday(data) {
      const now = new Date();
      const yesterday = new Date(now);
      yesterday.setDate(yesterday.getDate() - 1);
      return data.filter(item => {
        const itemDate = new Date(item.year, this.monthLabels.indexOf(item.month), item.day || 1);
        return itemDate.getFullYear() === yesterday.getFullYear() && itemDate.getMonth() === yesterday.getMonth() && itemDate.getDate() === yesterday.getDate();
      }).sort((a, b) => {
        return (a.hour || 0) - (b.hour || 0);
      });
    },
    /**
     * Formatea una fecha en formato corto (ej: "24 Mar")
     */
    formatShortDate(date) {
      return `${date.getDate()} ${this.monthLabels[date.getMonth()]}`;
    },
    /**
     * Calcula la posición Y para un valor dado
     */
    calculateYPosition(value, maxValue) {
      const paddingBottom = 20;
      const paddingTop = 10;
      const availableHeight = this.chartHeight - paddingBottom - paddingTop;
      console.log('Cálculo posición Y:', {
        valor: value,
        maxValue: maxValue,
        alturaDisponible: availableHeight,
        paddingBottom,
        paddingTop,
        posiciónCalculada: value ? this.chartHeight - paddingBottom - value / maxValue * availableHeight : this.chartHeight - paddingBottom
      });

      // Si el valor es undefined, null o 0, retornar la posición del eje X
      if (!value) {
        return this.chartHeight - paddingBottom;
      }

      // Para valores positivos, calcular la posición proporcional
      return this.chartHeight - paddingBottom - value / maxValue * availableHeight;
    }
  }
};
