      const cacheKey = `terms:${query}`;
      if (this.searchCache.has(cacheKey)) {
        return this.searchCache.get(cacheKey);
      }
      if (!query) return [];

      // Normalizar el texto (quitar acentos, convertir a minúsculas)
      const normalizedQuery = this.normalizeText(query);

      // Dividir en términos individuales y filtrar términos vacíos o muy cortos
      const terms = normalizedQuery.split(/\s+/).filter(term => term.length > 1).map(term => term.toLowerCase());

      // Guardar en caché
      this.updateCache(cacheKey, terms);
      return terms;
    },
    /**
     * Comprueba si un elemento coincide con los términos de búsqueda
     */
    itemMatchesSearch(item, queryTerms) {
      if (!queryTerms.length) return true;

      // Clave para caché (item id + query)
      const itemId = item.id || item.text;
      const cacheKey = `match:${itemId}:${queryTerms.join('|')}`;

      // Verificar si ya está en caché
      if (this.searchCache.has(cacheKey)) {
        return this.searchCache.get(cacheKey);
      }

      // Normalizar el texto del elemento
      const normalizedText = this.normalizeText(item.text).toLowerCase();

      // Verificar coincidencia exacta primero
      if (normalizedText.includes(queryTerms.join(' '))) {
        this.updateCache(cacheKey, true);
        return true;
      }

      // Verificar coincidencia de términos individuales
      // Un elemento coincide si contiene TODOS los términos de búsqueda
      const result = queryTerms.every(term => {
        // Coincidencia directa en el texto
        if (normalizedText.includes(term)) {
          return true;
        }

        // Coincidencia de inicio de palabra
        const words = normalizedText.split(/\s+/);
        return words.some(word => word.startsWith(term));
      });

      // Guardar en caché
      this.updateCache(cacheKey, result);
      return result;
    },
    /**
